"use strict";function Register(){this.code=document.querySelector(".code"),this.codeL=document.querySelector(".code .codeL"),this.code2=document.querySelector(".login .code2"),this.dot=document.querySelector(".state i"),this.btn=document.querySelector(".login .btn"),this.init()}Register.prototype={init:function(){this.bnt(),this.dis(),this.verify(),this.click()},bnt:function(){var t=this;this.codeL.onclick=function(){t.code.style.display="none",t.code2.style.display="block"}},dis:function(){this.dot.onclick=function(){this.style.cssText="background:url('../images/ico_checkbox_selected.png');background-size:100%;"}},verify:function(){var t=document.querySelector(".inputTxt2");t.onblur=function(){/^[a-zA-Z]\w{3,6}$/.test(t.value)||alert("请输入4-7位以字母开头密码")}},click:function(){var t=this;this.btn.onclick=function(){t.information()}},information:function(){var t=document.querySelector(".inputTxt1").value,e=document.querySelector(".inputTxt2").value;axios({method:"get",url:"http://10.9.48.34/h5/project/meizu/php/register.php",data:{uname:t,upwd:e}}).then(function(t){"1"==t.state?(alert(t.info),location.href="login.html"):alert(t.info)}).catch(function(t){console.log(t)})}},new Register;